class HashMixTest extends \PHPUnit_Framework_TestCase
{
    /**
     * @var \Bloom\Hash\HashMix
     */
    protected $object;
    /**
     * @var \Bloom\Hash\HashMix
     */
    protected $hashmix;

    protected $_cycles = 10000;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        $this->hashmix = new \Bloom\Hash\HashMix;
    }

    /**
     * 2.6631093025208E-5/operation
     */
    public function testHashMix()
    {
        echo PHP_EOL;
        $str = 'Robert Allen';
        $start = microtime(true);
        for($i = 0; $i < $this->_cycles; $i++){
            $value = $this->hashmix->hash($str, $i);
            if($value < 1){
                echo $i, '::', $value, PHP_EOL;
            }
        }
        $end = microtime(true);
        echo ($end - $start) / $this->_cycles, PHP_EOL;
    }
}
